package model;

public class MathHere {

	public MathHere() {
		// TODO Auto-generated constructor stub

	}

	public int Addition(int x, int y) {
		return x + y;
	}

	public int Subtraction(int x, int y) {
		return x - y;
	}
	
	public int Multiplication(int x, int y) {
		return x * y;
	}

	public double Division(double x, double y) {
		return x / y;
	}
	

}
